input.json_schema:
{
  "type": "object",
  "properties": {
    "cidr_block": {
      "type": "string",
      "description": "The CIDR block or IP address that needs to be blocked (e.g., 203.0.113.0/24 or 192.168.1.1)"
    },
    "network_region": {
      "type": "string",
      "description": "The network region or tag identifier (e.g., US-East-Region, region:east-coast)"
    },
    "incident_summary": {
      "type": "string",
      "description": "Brief summary of the security incident requiring the firewall block"
    }
  },
  "required": ["cidr_block", "network_region"]
}
