import re
import sys

# Get inputs from command line arguments
(appliance_current_version, switch_current_version, wireless_current_version, 
 appliance_input_version, switch_input_version, wireless_input_version) = sys.argv[1:7]

# Extract version IDs from formatted strings like 'MX 18.211.5.2 (ID:4625)'
def extract_version_id(version_string):
    if not version_string or version_string.strip() == '' or version_string == 'null':
        return ''

    # Look for pattern like '(ID:1234)' in the string
    match = re.search(r'\(ID:([^)]+)\)', version_string)
    if match:
        return match.group(1)
    return ''

# Extract version names from formatted strings like 'MX 18.211.5.2 (ID:4625)'
def extract_version_name(version_string):
    if not version_string or version_string.strip() == '' or version_string == 'null':
        return ''

    # Extract everything before ' (ID:' or return the whole string if no ID found
    if ' (ID:' in version_string:
        return version_string.split(' (ID:')[0]
    return version_string

# Extract current version IDs and names from each device type
applianceCurrentVersionId = extract_version_id(appliance_current_version)
switchCurrentVersionId = extract_version_id(switch_current_version)
wirelessCurrentVersionId = extract_version_id(wireless_current_version)

applianceCurrentVersionName = extract_version_name(appliance_current_version)
switchCurrentVersionName = extract_version_name(switch_current_version)
wirelessCurrentVersionName = extract_version_name(wireless_current_version)

# Initialize status tracking
status_messages = []

# Determine filtered version IDs based on upgrade requirements
# Set to input value if upgrade needed, empty if not needed or input is empty
filteredApplianceVersionId = ''
filteredSwitchVersionId = ''
filteredWirelessVersionId = ''

# Check appliance upgrade requirement
if appliance_input_version and appliance_input_version.strip() != '' and appliance_input_version != 'null':
    if appliance_input_version == applianceCurrentVersionId:
        status_messages.append(f' Appliance (MX): Already running target version (ID: {appliance_input_version}) - upgrade skipped')
    else:
        filteredApplianceVersionId = appliance_input_version
        if applianceCurrentVersionName:
            status_messages.append(f' Appliance (MX): Upgrade scheduled from {applianceCurrentVersionName} to version ID {appliance_input_version}')
        else:
            status_messages.append(f' Appliance (MX): Upgrade scheduled to version ID {appliance_input_version}')

# Check switch upgrade requirement  
if switch_input_version and switch_input_version.strip() != '' and switch_input_version != 'null':
    if switch_input_version == switchCurrentVersionId:
        status_messages.append(f' Switch (MS): Already running target version (ID: {switch_input_version}) - upgrade skipped')
    else:
        filteredSwitchVersionId = switch_input_version
        if switchCurrentVersionName:
            status_messages.append(f' Switch (MS): Upgrade scheduled from {switchCurrentVersionName} to version ID {switch_input_version}')
        else:
            status_messages.append(f' Switch (MS): Upgrade scheduled to version ID {switch_input_version}')

# Check wireless upgrade requirement
if wireless_input_version and wireless_input_version.strip() != '' and wireless_input_version != 'null':
    if wireless_input_version == wirelessCurrentVersionId:
        status_messages.append(f' Wireless (MR): Already running target version (ID: {wireless_input_version}) - upgrade skipped')
    else:
        filteredWirelessVersionId = wireless_input_version
        if wirelessCurrentVersionName:
            status_messages.append(f' Wireless (MR): Upgrade scheduled from {wirelessCurrentVersionName} to version ID {wireless_input_version}')
        else:
            status_messages.append(f' Wireless (MR): Upgrade scheduled to version ID {wireless_input_version}')

# Determine if any upgrades are needed
anyUpgradeNeeded = (filteredApplianceVersionId != '' or 
                   filteredSwitchVersionId != '' or 
                   filteredWirelessVersionId != '')

# Generate final status message
if not status_messages:
    upgrade_status_message = ' No firmware upgrade targets specified - all device type version fields are empty.'
else:
    upgrade_status_message = '\n'.join(status_messages)
    if anyUpgradeNeeded:
        upgrade_count = sum(1 for vid in [filteredApplianceVersionId, filteredSwitchVersionId, filteredWirelessVersionId] if vid != '')
        skip_count = len(status_messages) - upgrade_count
        if skip_count > 0:
            upgrade_status_message += f'\n\n Summary: {upgrade_count} device type(s) scheduled for upgrade, {skip_count} already at target version(s)'
        else:
            upgrade_status_message += f'\n\n Summary: {upgrade_count} device type(s) scheduled for upgrade'
    else:
        upgrade_status_message += '\n\n Summary: All specified device types are already running target firmware versions - no upgrades needed'
